@use '../abstracts' as a;
@use '../functions/' as *;


.navbar {
    &__container {
        display:flex;
        justify-content: space-between;
        margin-right: rem(25);
        margin-left: rem(25);
    }

    &__brand {
        font-family: a.$primary-font;
        color: a.$marvel-red;
        font-size: rem(40);
        font-weight: 700;
    }
    &__links {
        display: none;
    }

}

//responsive media queries

@media only screen and (min-width: 768px) {

    .navbar {
        &__mobile-button {
            display: none;
        }

        &__links {

            display: inline-block;
            > ul {
                display: flex;
                list-style-type: none;

                > li {
                    font-size: rem(18);
                    padding-right: rem(20);
                    font-family: a.$secondary-font;
                }
            }

        }
    }
    
}

