sources.components\\_accordian.scss:
@use '../abstracts' as a;
@use '../functions/' as *;

.accordian {
    position: relative;
    background-color: a.$section-color;
    margin-bottom: rem(80);
    padding-top: rem(16);
    padding-left: rem(50);
    padding-right: rem(50);
    min-height: rem(650);
    cursor: pointer;

    &__description {
        margin-bottom: rem(50);
    }

    h1 {
        text-align: center;
    }

    &__section {
        margin-bottom: rem(20);
        .button {
            span {
                transition-duration: 200ms;
            }
        }
        .panel {
            display: none;
            padding: rem(20);
        }
        ul {
            margin: 0;
        }
        h2 {
            border-bottom: 1px solid rgba(0, 0, 0, 0.18);
            margin: 0;
        }
        span {
            float: right;
        }
        #h2LastChild {
            border: hidden;
        }
    }

}

.open {
    span {
            transform: rotate(180deg);
            transition-duration: 200ms;
    }
}

@media only screen and (min-width: 1024px) {
    .accordian {
        max-width: rem(1300);
        margin: 0 auto;
        margin-bottom: rem(80);
        border-radius: rem(30);
        padding-top: 0;
        border: 1px solid rgba(0, 0, 0, 0.1);
        box-shadow: 0px 4px 50px rgba(0, 0, 0, 0.17);

        &__section {
            ul {
                -webkit-column-count: 3;
                -moz-column-count: 3;
                column-count: 3;
            }
        }
    }
}
