sources.components\\_nav.scss:
@use '../abstracts' as a;
@use '../functions/' as *;


.navbar {
    background-color: #fff;
    box-shadow: 1px 1px 4px 0 rgba(0,0,0,.1);
    position: fixed;
    width: 100%;
    z-index: 3;

    &__container {
        display:flex;
        flex-wrap: wrap;
        justify-content: space-between;
        align-items: center;
        margin-right: rem(25);
        margin-left: rem(25);
    }

    &__mobile-button {
        z-index: 1;
    }

    &__brand {
        font-family: a.$primary-font;
        color: a.$marvel-red;
        font-size: rem(40);
        font-weight: 700;

    }
    &__links {
        position: absolute;
        top: -800%; //position off screen for transition effect

        &__open { //open hamburger menu
        display: flex;
        flex-wrap: wrap;
        width: 100%;
        height: 100vh;
        align-items: center;
        justify-content: center;
        top: 0;
        left: 0;
        background-color: white;
        transition-timing-function: ease-in-out;
        transition-duration: 200ms;
        z-index: -1;
        }

        > ul {
            list-style-type: none;
        }
        &__item {
            text-decoration: none;
            text-transform: uppercase;
            font-family: a.$primary-font;
            color: a.$marvel-red;
            font-size: rem(50);
            font-weight: 700;
            letter-spacing: 1px;
        }
    }
}

// responsive media quieries for tablet
@media only screen and (min-width: 768px) {

    .navbar {
        &__mobile-button {
            display: none;
        }

        &__links {
            display: inline-block;
            position: relative;
            > ul {
                display: flex;
                list-style-type: none;

                > li {
                    font-size: rem(18);
                    padding-right: rem(20);
                    font-family: a.$secondary-font;
                }
            }
            &__item {
                font-size: rem(25);
            }

        }
    }
    
}

@media only screen and (min-width: 1400px) {
    .navbar {
        &__brand {
            font-size: rem(60);
        }
        &__links {
            > ul {
                > li {
                    font-size: 1.50rem;
                }
            }
        }
    }
}

sources.components\\_hero.scss:
@use '../abstracts' as a;
@use '../functions/' as *;


.hero {
    max-height: 500px;
    &__top {
        background: #24768C;
        display: flex;
        justify-content: center;
        position: relative;
        #avengers {
            max-width: 100%;
            position: relative;
            top: rem(15);
            z-index: 1;
            margin-top: rem(50);
        }
        &::before {
            content: "";
            background: url("../img/Hero-ray.svg");
            background-size: cover;
            position: absolute;
            width: 100%;
            height: 100%;
        }
        &::after {
            position: absolute;
            content: "";
            background: url("../img/Hero-city.svg");
            background-size: cover;
            background-repeat: no-repeat;
            background-position-x: center;
            background-position-y: bottom;  
            width: 100%;
            height: 100%;

        }
    }
    &__bottom {
        display: flex;
        justify-content: center;
        align-items: center;
        background-color: a.$marvel-red;
        font-family: a.$primary-font;
        font-weight: 700;
        letter-spacing: rem(1.5);
        color: a.$secondary-font-color;
        height: rem(64);
        margin-bottom: rem(80);
        font-size: clamp(1rem, 6vw, 3rem);
    }
}

@media only screen and (min-width: 1024px) {

    .hero {
        &__top {
            #avengers {
                width: 60%;
                top: rem(20);
            }
        }
        &__bottom {
            //font-size: rem(40);
        }
    }

}


@media only screen and (min-width: 2000px) {

    .hero {
        &__top {
            #avengers {
                width: 40%;
                top: rem(48);
            }
        }
        &__bottom {
            //font-size: rem(50);
            height: 6rem;
        }
    }

}

sources.components\\_cards.scss:
@use '../abstracts/' as a;
@use '../functions/' as *;

.resources {
    position: relative;
    background-color: a.$section-color;
    padding-top: rem(70);
    padding-bottom: rem(30);
    margin-top: rem(80);
    margin-bottom: rem(80);

    &::before {
        content: "Resources";
        font-family: a.$primary-font;
        color: a.$marvel-red;
        font-size: rem(46);
        font-weight: 700;
        position:absolute;
        top: -2.5%;
        right: 5%;
    }

    &__container {
        display: flex;
        justify-content: center;
        align-items: center;
        flex-wrap: wrap;
        gap: 5%;
    }

    &__card {
        background-color: white;
        max-width: rem(240);
        border-radius: rem(16);
        border: 0.558491px solid rgba(0, 0, 0, 0.2);
        box-shadow: 4.38685px 4.87428px 22.3396px rgba(0, 0, 0, 0.2);
        margin-bottom: rem(25);

        &__icon {
            border-radius: rem(16);
            max-height: rem(150);
            display:flex;
            align-items: center;
            justify-content: center;

            > img {
                width: rem(195);
                display:flex;
                align-items: center;
                justify-content: center;
            }

            &--purple {
                background-color: a.$hawkeye-bg-color;
            }
            &--green {
                background-color: a.$hulk-bg-color;
            }
            &--blue {
                background-color: a.$thor-bg-color;
            }
        }

        &__title {
            padding-top: rem(25);
            padding-bottom: rem(10);
            text-align:center;
            font-size: rem(25);
            font-weight: 800;
            font-family:a.$primary-font;
        }

        &__description {
            font-size: rem(18);
            padding-left: rem(20);
            padding-right: rem(20);
            padding-bottom: rem(20);
        }
    }

}

//consider moving this section to another stylesheet
.about {
    position: relative;
    background-color: a.$section-color;
    margin-bottom: rem(80);
    padding-top: rem(70);
    &__container {
        display: flex;
        flex-wrap: wrap;
        align-items: center;
        justify-content: space-around;


        &::before {
            content: "About Me";
            font-family: a.$primary-font;
            color: a.$marvel-red;
            font-size: rem(46);
            font-weight: 700;
            position:absolute;
            top: -1.5%;
            right: 5%;
        }
    }

    &__text {
        width: 80%;
        margin-top: rem(50);
        margin-bottom: rem(50); 
    }
}

@media only screen and (min-width: 1230px) {

    .resources {
        max-width: rem(1300);
        margin: 0 auto;
        margin-bottom: rem(80);
        border-radius: rem(30);
        margin-top: rem(96);
        border: 1px solid rgba(0, 0, 0, 0.1);
        box-shadow: 0px 4px 50px rgba(0, 0, 0, 0.17);

        &::before {
            top: -5%;
        }
    }

    .about {
        max-width: rem(1300);
        margin: 0 auto;
        margin-bottom: rem(80);
        border-radius: rem(30);
        padding-top: 0;
        border: 1px solid rgba(0, 0, 0, 0.1);
        box-shadow: 0px 4px 50px rgba(0, 0, 0, 0.17);

        &__container {
            &::before {
                top: -2.5%;
            }
        }

        &__text {
            max-width: 50%;
        }

        #manny-cartoon {
            width: 135%;
        }
    }
}

